	CALL get_partial_charges(RundI_H,N_Resids,GesamtanzahlH,residue_namesH,partial_charge_methode,&
                     &q_n_el_lo,trajstep,KETTE,lambda_out_filename_el,lambda_out_filename_lo,foldername)
