                exit
            end if
        end do
        close(7)
        
        
    ELSE IF (read_dftb_plus) THEN ! read data from dftb+ (formated) output
    
		!!! -----------------------------------------------------------
		!!! read_dftb_plus
		!!! -----------------------------------------------------------
		write(*,*) 'Start reading DIPRO dftb+ input.'
		IF( file_exists('./overreal.dat'))then
			eigenvectorfile='eigenvec.out'
			detailedfile='detailed.out'
