		    GesamtanzahlH=GesamtanzahlH+N_PPDI*96
		ELSE 
		    GesamtanzahlH=GesamtanzahlH+N_PPDI*78 ! Anzahl mit H-gruppen
		END IF !PPDI
		
		N_CarboxyS2=8
