		i_ende=R_u_I(N_molecules_acceptor+1,2)-1  ! offset ggf anpassen !!!
		WRITE(*,*) 'i_ende:',i_ende

	IF(DIPBI_P3HT_calc) THEN !!! unite P3HT or PBDT-TS1 units 
		    !!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!! 
		    !!!! Section unite all P3HT units to one entire molecule and add H atoms to 32mer  !!!!!!
		    !!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!	
