			!WRITE(*,*) ind1,R_u_I(ind1,1),R_u_I(ind1,2),ind1,i_max32_mer,residue_names(ind1),residue_names(ind1+1)

			CALL add_h_atoms_to_mol(atomsorteA(i_start:i_ende),koordA(i_start:i_ende,1:3),&
