		   ! WRITE(*,*) 'GesamtanzahlH: ',GesamtanzahlH,' Gesamtanzahl: ',Gesamtanzahl
		   IF(print_coordH_to_xyz) THEN !ok
				WRITE(*,*) ' print_coordH_to_xyz ' 
