			IF (DEBUG) WRITE(*,*) Resid_and_S(1,i),TRIM(sequence_list(i)),Resid_and_S(2,i),residue_name_site(i),'END PBDT-TS1'
			
        ELSE IF  (thiophene_found) THEN !!! P3HT / P3MT!P3HT
				
