    box_size(3)=z_box
    DO i=1,3
        IF( dist(i) > 0.5*box_size(i) ) THEN
                dist(i) = dist(i)-box_size(i)
        ELSE IF ( dist(i) < -0.5*box_size(i) ) THEN
                dist(i) = dist(i)+box_size(i)
