           schwerpunkt(:)=schwerpunkt(:)+m_i*koord(i,:)     
           m_ges=m_ges+m_i     
           !write(*,*) m_i,schwerpunkt
        end do 
        schwerpunkt=schwerpunkt/real(m_ges)
        ! Berechnung der Anzahl der Elektronen im neutralen Molekl z_ges 
        z_ges=0
        do i=1,MAXATM
           z_i=0
           ! .gro Gruppen
           if (TRIM(adjustl(atomsorte(i)))=='HC')  z_i=1    ! fuer H
