	WRITE(50,*) '     graphics $mol cylinder $start $middle radius 0.15               '
	WRITE(50,*) '     graphics $mol cone $middle $end radius 0.25                     '
	WRITE(50,*) ' }                                                                   '
	WRITE(50,*) '  color scale method RWB '
