	subroutine loadNetwork()
		use small_functions
		implicit none
		type(verbindung_d)::verbindung
		Character(50) :: punkt1
		Character(50) :: punkt2
		Real*8        :: kosten
		integer       :: total_anzahl_knoten
		integer       :: total_anzahl_verbindungen
		integer       :: i_knoten,punkt1_id
