				enddo
			enddo
			write(*,*) '--------------------------------------------------------'
		end if
		
		!! initialisierung Start / Ziel Graph, setze hier defaults.
		Startknoten = Graph%knoten(1)
		Zielknoten  = Graph%knoten(2)
		
		
		
	end subroutine loadNetwork 


